import { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import reactLogo from '../../assets/logo512.png';
import docker from '../../assets/docker.png';
import github from '../../assets/github.png';
import java from '../../assets/java.png';
import python from '../../assets/python.png';
import stackoverflow from '../../assets/stackoverflow.svg';
import './Grid.css';
import Rounds from '../Rounds';

type Props = {
  index: number;
  handleCount: Function;
};

export function Rutenett({ index, handleCount }: Props) {
  const [image, setImage] = useState<string>();

  useEffect(() => {
    const images = [reactLogo, docker, github, java, python, stackoverflow];
    setImage(images[index - 1]);
  }, [index]);

  const board = buildBoard();

  return (
    <>
      <RoundTitle>{`Runde ${index}/6`}</RoundTitle>
      <div>
        <img
          src={image}
          className='App-logo'
          alt='logo'
          style={{ maxWidth: '80%', border: '3px dashed green' }}
        />
        <div id='grid-base'>{board}</div>
      </div>
      <Rounds fraParent={handleCount} />
    </>
  );
}

export const RoundTitle = styled.h2`
  font-family: 'Neue Machina';
  font-size: 1.2vw;
  margin-bottom: 1.5vw;
`;

const fadeOut = keyframes`
from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
`;

interface CellProps {
  cellNumber: number;
  delayTime: number;
}

function buildBoard() {
  const Cell = styled.div<CellProps>`
    animation: ${fadeOut} 2s linear forwards;
    animation-delay: ${(props) => props.delayTime + 's'};
    background-color: white;
    border: 1px solid #ff4b34;
  `;

  let board = [];

  const timeDelays = Array(400)
    .fill(400)
    .map((_, i) => i + 1);
  timeDelays.sort(() => Math.random() - 0.5);

  for (let row = 0; row < 20; row++) {
    for (let column = 0; column < 20; column++) {
      const number = row + 1 + column + 2;
      const timeDelay: number = timeDelays.pop()!;
      board.push(<Cell cellNumber={number} delayTime={timeDelay} />);
    }
  }

  return board;
}
